import React, { useState } from "react";

import MovieList from "./components/MovieList";
import Filter from "./components/Filter";
import AddNewMovie from "./components/AddNewMovie";

import { v4 as uuidv4 } from "uuid";
import "./App.css";
import MovieDescription from "./components/MovieDescription";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Routes,
} from "react-router-dom";

const App = () => {
  const [movies, setMovies] = useState([
    {
      id: uuidv4(),
      title: "La Casa de Papel",
      description:
        "Une équipe de voleurs très préparée entre dans la Monnaie royale d'Espagne pour voler 2,4 milliards d'euros en utilisant un plan sophistiqué et sans violence. Le chef du gang, le Professeur, recrute huit personnes pour les aider à effectuer l'opération.",
      posterURL:
        "data:image/jpeg;base64,",
      rating: 3,
      trailerLink:
        "https://www.youtube.com/embed/emo8VqrXEyc?si=O2KVazL9nA0c_xjj",
    },
    {
      id: uuidv4(),
      title: "Macha",
      description:
        "Masha et Michka suit les aventures de Masha, une jeune fille espiègle, et de Michka, un ours attentionné, alors qu'ils naviguent à travers des situations comiques et attachantes dans la forêt",
      posterURL:
        "data:image/jpeg;base64,",
      rating: 4,
      trailerLink:
        "https://www.youtube.com/embed/Yy0pLfRUYGM?si=PuczIerD4BvRrYR1",
    },
    {
      id: uuidv4(),
      title: "Titanic",
      description:
        "Un jeune artiste sans le sou et une jeune aristocrate tombent amoureux à bord du paquebot en perdition. Ils doivent affronter la tragédie imminente lorsque le navire heurte un iceberg.",
      posterURL:
        "data:image/jpeg;base64,",
      rating: 5,
      trailerLink:
        "https://www.youtube.com/embed/arSjWhqMGQM?si=vpk_kJoYUQF9N5Zm",
    },
    {
      id: uuidv4(),
      title: "Wrong Turn",
      description:
        "Une jeune femme et ses amis font face à des horreurs indicibles lorsqu'ils se retrouvent coincés dans une forêt habitée par des cannibales déformés.",
      posterURL:
        "https://miro.medium.com/v2/resize:fit:686/1*XNrlKbwwAn3oisuPuUupmw.jpeg",
      rating: 5,
      trailerLink:
        "https://www.youtube.com/embed/8qp6BWG0V8I?si=zl5m-iOxQ-aE-xt3",
    },
    {
      id: uuidv4(),
      title: "Breaking Bad",
      description:
        "Breaking Bad raconte l'histoire de Walter White, un professeur de chimie atteint d'un cancer du poumon inopérable, qui, avec son ancien élève, Jesse Pinkman, décide de fabriquer et de vendre de la méthamphétamine pour assurer l'avenir financier de sa famille.",
      posterURL:
        "data:image/jpeg;base64,",
      rating: 5,
      trailerLink:
        "https://www.youtube.com/embed/WhJCKN2hu5k?si=OfGr5IQ2pU9M2Yyp",
    },
  ]);

  const [filteredMovies, setFilteredMovies] = useState(movies);
  const [rating, setRating] = useState(1);

  const handleAddMovie = (newMovie) => {
    setMovies([...movies, newMovie]);
    setFilteredMovies([...filteredMovies, newMovie]);
  };

  const handleDelete = (id) => {
    setMovies(movies.filter((movie) => movie.id !== id));
    setFilteredMovies(filteredMovies.filter((movie) => movie.id !== id));
  };

  const handleFilterChange = (title, selectedRating) => {
    let filtered = [...movies];

    if (title) {
      filtered = filtered.filter((movie) =>
        movie.title.toLowerCase().includes(title.toLowerCase())
      );
    }

    if (selectedRating) {
      filtered = filtered.filter((movie) => movie.rating === selectedRating);
    }

    setFilteredMovies(filtered);
  };

  const handleEdit = (editedMovie) => {
    const updatedMovies = movies.map((movie) =>
      movie.id === editedMovie.id ? editedMovie : movie
    );
    setMovies(updatedMovies);
    setFilteredMovies(updatedMovies);
  };
  const handleRatingChange = (value) => {
    setRating(value);
  };

  return (
    <div>
      <h1>My Movie App</h1>
      <Router>
        <Filter
          handleFilterChange={handleFilterChange}
          handleRatingChange={handleRatingChange}
          rating={rating}
        />
        <Routes>
          <Route
            path="/"
            element={
              <MovieList
                movies={movies}
                movies={filteredMovies}
                deleteFunction={handleDelete}
                handleEdit={handleEdit}
                handleRatingChange={handleRatingChange}
              />
            }
          />
          <Route
            path="/movie/:id"
            element={<MovieDescription movies={movies} />}
          />
        </Routes>
        <AddNewMovie handleAddMovie={handleAddMovie} />
      </Router>
    </div>
  );
};

export default App;
